mapper.select:
SELECT id, name, age, email
FROM test_user
WHERE id = #{id}

SELECT id, name, age, email
FROM test_user
WHERE name = #{name}

mapper.update:
UPDATE test_user
SET name=#{name}, age=#{age}, email=#{email}
WHERE id = #{id}

mapper.insert:
INSERT INTO test_user
(name, age, email)
VALUES(#{name}, #{age}, #{email});
